import React, { Component } from 'react'
import EmployeeService from '../services/EmployeeService'
import img1 from '../components/img/paisagem1.png'

class ListEmployeeComponent extends Component {
 
constructor(props) {
super(props)

 this.state = {
employees: []
}
this.addEmployee = this.addEmployee.bind(this);
this.editEmployee = this.editEmployee.bind(this);
this.deleteEmployee = this.deleteEmployee.bind(this);
}

 deleteEmployee(id){
EmployeeService.deleteEmployee(id).then( res => {
this.setState({employees: this.state.employees.filter(employee => employee.id !== id)});
});
}
viewEmployee(id){
this.props.history.push(`/view-employee/${id}`);
}
editEmployee(id){
this.props.history.push(`/add-employee/${id}`);
}

 componentDidMount(){
EmployeeService.getEmployees().then((res) => {
this.setState({ employees: res.data});
});
}

 addEmployee(){
this.props.history.push('/add-employee/_add');
}

 render() {
return (
<div>
    <h2 className="text-center">Employees List</h2>
    <div className = "row">
        <button className="btn btn-primary" onClick={this.addEmployee}> Add Employee</button>
    </div>
<br></br>
<div style={{padding: "50px"}} className = "container body-content">

<h1 >VOOS COM DESCONTO</h1>
<div style={{padding: "100px"}}>
            <div id="carouselExampleControls" class="carousel slide"
                data-bs-ride="carousel">
                <div class="carousel-inner">
                    <div class="carousel-item active">
                        <div class="row row-cols-1 row-cols-md-2 g-4">

                            {this.state.employees.map(employee =>
                            <div class="col">
                                <div class="card">
                                    <img src={img1} class="card-img-top" alt="..." />
                                    <div class="card-body">
                                        <tr key = {employee.id}>
                                            <h5 class="card-title">{ employee.lugares}</h5>
                            
                                            <p class="card-text">
                                            De <strike>$ {employee.valores}</strike> por apenas ${employee.promocao}</p>
                                            <button onClick={ () => this.editEmployee(employee.id)} className="btn btn-info">Update </button>
                                            <button style={{marginLeft: "10px"}} onClick={ () => this.deleteEmployee(employee.id)} className="btn btn-danger">Delete </button>
                                            <button style={{marginLeft: "10px"}} onClick={ () => this.viewEmployee(employee.id)} className="btn btn-info">View </button>

                                </tr>
                                </div>
                            </div>
                        </div>
                        )}
</div>
</div>
</div>
</div>
</div>
 </div>

 </div>
)
}
}

export default ListEmployeeComponent
